import React, { useEffect, useRef } from 'react'
import * as echarts from 'echarts'
import { px } from '../../shared/px'
import { BaseEchartsOptions } from '../../shared/base-echarts-options'
import './index.scss'
export default function PieChartTest () {
  const divRef = useRef(null)
  useEffect(() => {
    var myChart = echarts.init(divRef.current)
    var option = {
      color: ['#5fb878', '#eeeeee'],
      series: [
        {
          name: '访问来源',
          type: 'pie',
          radius: ['50%', '70%'],
          avoidLabelOverlap: false,
          hoverAnimation: false, //关闭放大动画
          selectedOffset: 0, //选中块的偏移量
          label: {
            show: false,
            position: 'center',
            formatter: '{d}%'
          },
          emphasis: {
            label: {
              show: true,
              fontSize: px(20),
              fontWeight: 'bold'
            }
          },
          labelLine: {
            show: false
          },
          data: [
            {
              value: 435,
              name: '直接访问',
              selected: true, //默认选中第一块
              label: {
                show: true, //默认显示第一块
                fontSize: px(20),
                fontWeight: 'bold'
              }
            },
            { value: 310, name: '邮件营销' }
          ]
        }
      ]
    }
    myChart.setOption(option)
  }, [])
  return (
    <div className='yield-piechart'>
      <div className='chart'>
        <div ref={divRef} className='main' />
        {/* <div className='text'>50%</div> */}
      </div>
      <div className='legend'>业务</div>
    </div>
  )
}
