import React, { useEffect, useRef } from 'react'
import * as echarts from 'echarts'
import { px } from '../shared/px'
import { BaseEchartsOptions } from '../shared/base-echarts-options'

export default function Chart7 () {
  const divRef = useRef(null)
  useEffect(() => {
    var myChart = echarts.init(divRef.current)
    var categories = []
    for (var i = 0; i < 2; i++) {
      categories[i] = {
        name: '类目' + i
      }
    }
    var option = {
      ...BaseEchartsOptions,
      // 图的标题
      title: {
        text: 'ECharts 关系图'
      },
      // 提示框的配置
      tooltip: {
        formatter: function (x) {
          return x.data.des
        }
      },
      // 工具箱
      toolbox: {
        // 显示工具箱
        show: true,
        feature: {
          mark: {
            show: true
          },
          // 还原
          restore: {
            show: true
          },
          // 保存为图片
          saveAsImage: {
            show: true
          }
        }
      },
      legend: [
        {
          // selectedMode: 'single',
          data: categories.map(function (a) {
            return a.name
          })
        }
      ],
      series: [
        {
          type: 'graph', // 类型:关系图
          layout: 'force', //图的布局，类型为力导图
          symbolSize: 40, // 调整节点的大小
          roam: true, // 是否开启鼠标缩放和平移漫游。默认不开启。如果只想要开启缩放或者平移,可以设置成 'scale' 或者 'move'。设置成 true 为都开启
          edgeSymbol: ['circle', 'arrow'],
          edgeSymbolSize: [2, 10],
          edgeLabel: {
            normal: {
              textStyle: {
                fontSize: px(20)
              }
            }
          },
          force: {
            repulsion: 2500,
            edgeLength: [10, 50]
          },
          draggable: true,
          lineStyle: {
            normal: {
              width: px(2),
              color: '#4b565b'
            }
          },
          edgeLabel: {
            normal: {
              show: true,
              formatter: function (x) {
                return x.data.name
              }
            }
          },
          label: {
            normal: {
              show: true,
              textStyle: {}
            }
          },

          // 数据
          data: [
            {
              name: 'node01',
              des: 'nodedes01',
              symbolSize: px(70),
              category: 0
            },
            {
              name: 'node02',
              des: 'nodedes02',
              symbolSize: px(50),
              category: 1
            },
            {
              name: 'node03',
              des: 'nodedes3',
              symbolSize: px(50),
              category: 1
            },
            {
              name: 'node04',
              des: 'nodedes04',
              symbolSize: px(50),
              category: 1
            },
            {
              name: 'node05',
              des: 'nodedes05',
              symbolSize: px(50),
              category: 1
            }
          ],
          links: [
            {
              source: 'node01',
              target: 'node02',
              name: 'link01',
              des: 'link01des'
            },
            {
              source: 'node01',
              target: 'node03',
              name: 'link02',
              des: 'link02des'
            },
            {
              source: 'node01',
              target: 'node04',
              name: 'link03',
              des: 'link03des'
            },
            {
              source: 'node01',
              target: 'node05',
              name: 'link04',
              des: 'link05des'
            }
          ],
          categories: categories
        }
      ]
    }
    myChart.setOption(option)
  }, [])
  return (
    <div class='bordered guanxi'>
      <h2>双代号时标网络图</h2>
      <div ref={divRef} className='chart'></div>
    </div>
  )
}
