import React, { useRef, useEffect } from 'react'
import * as echarts from 'echarts'
import { px } from '../../shared/px'
import { BaseEchartsOptions } from '../../shared/base-echarts-options'
import './index.scss'
//'#3fb1e3''#6be6c1''#626c91''#a0a7e6''#c4ebad''#96dee8'
export default function ProductInventoryChart () {
  const divRef = useRef(null)
  const myChart = (null)
  const testData = [
    { name: '甲', value: 2 },
    { name: '乙', value: 3 },
    { name: '丙', value: 4 },
    { name: '丁', value: 5 },
    { name: '戊', value: 6 },
    { name: '己', value: 7 }
  ]
  useEffect(() => {
    setTimeout(() => {
      const newData = [
        { name: '甲', value: 7 },
        { name: '乙', value: 6 },
        { name: '丙', value: 5 },
        { name: '丁', value: 4 },
        { name: '戊', value: 3 },
        { name: '己', value: 2 }
      ]
    }, 3000)
  }, [])
  

  const renderMyChart = data => {
    var options = {
      ...BaseEchartsOptions,
      grid: {
        x: px(45),
        y: px(40),
        x2: px(20),
        y2: px(40)
      },
      xAxis: {
        data: testData.map(i => i.name),
        axisLabel: {
          fontSize: px(18),
          formatter (val) {
            if (val.length > 2) {
              const array = val.split('')
              array.splice(2, 0, '\n')
              return array.join('')
            } else {
              return val
            }
          }
        }
      },
      yAxis: {
        splitLine: { show: false },
        axisLabel: {
          fontSize: px(18)
        }
      },
      color: ['#ffb248'],
      series: [
        {
          type: 'bar',
          data: testData.map(i => i.value),
          showBackground: true,
          backgroundStyle: {
            color: '#ffb24820'
          }
        }
      ]
    }
    myChart.current.setOption(options)
  }

  useEffect(() => {
    myChart.current = echarts.init(divRef.current)//对myChart的ref的初始化
    renderMyChart(testData)
  }, [])
  return (
    <div className='bordered product-inventory-chart'>
      <h2>产品库存</h2>
      <div ref={divRef} className='chart'></div>
    </div>
  )
}
